import React from "react";
import { useLocation, useHistory } from "react-router-dom";
import { ConcreteColors } from "hcss-components";
import { GlobalSideNavigation } from "hcss-navigation/components/global-navigation";
import { SideNavigationMenuItem } from "hcss-navigation/components/global-navigation/side-navigation/items";
import { CreateItem } from "hcss-navigation/components/global-navigation/side-navigation/items/create";

export const SideNavigation = () => {
  const currentPath = useLocation().pathname;
  const history = useHistory();

  return (
    <GlobalSideNavigation
      topOffset={0}
      style={{
        backgroundColor: ConcreteColors.gray200
      }}
      createButtonComponent={<CreateItem />}
      primaryItems={[
        <SideNavigationMenuItem
          onClick={() => history.push("/")}
          isSelected={currentPath === "/"}
          icon={<DashboardIcon isSelected={currentPath === "/"} />}
        />,
        <SideNavigationMenuItem
          onClick={() => history.push("/projects")}
          isSelected={currentPath.startsWith("/projects")}
          icon={
            <ProjectsIcon isSelected={currentPath.startsWith("/projects")} />
          }
        />,
        <SideNavigationMenuItem
          onClick={() => history.push("/estimates")}
          isSelected={currentPath.startsWith("/estimates")}
          icon={
            <EstimatingIcon isSelected={currentPath.startsWith("/estimates")} />
          }
        />,
        <SideNavigationMenuItem
          onClick={() => history.push("/quotes")}
          isSelected={currentPath.startsWith("/quotes")}
          icon={<QuotesIcon isSelected={currentPath.startsWith("/quotes")} />}
        />,
        <SideNavigationMenuItem
          onClick={() => history.push("/messages")}
          isSelected={currentPath.startsWith("/messages")}
          icon={
            <MessagesIcon isSelected={currentPath.startsWith("/messages")} />
          }
        />,
        <SideNavigationMenuItem
          onClick={() => history.push("/contacts")}
          isSelected={currentPath.startsWith("/contacts")}
          icon={
            <ContactsIcon isSelected={currentPath.startsWith("/contacts")} />
          }
        />
      ]}
      secondaryItems={[]}
    />
  );
};

